mapper.select:
select *
from personi

    
        id =#{id}
    
    
        and grade like "%"#{grade}"%"
    
    
        and major like "%"#{major}"%"
    
    
        and name =#{name}
    
    
        and status=#{status}
    
    
        and det_time=#{det_time}
    
    
        and cul_people=#{cul_people}
    
    
        and qua=#{qua}
    
    
        and birthday>=#{birthday}
